mapper.select:
SELECT i.ingredient_id as id, i.category, i.name, i.name_eng, i.sale, i.inventory, i.sale_status, i.flavor, i.explanation, i.create_dt, i.update_dt FROM INGREDIENT i INNER JOIN ( SELECT ingredient_id, category, name, name_eng, sale, inventory, sale_status, flavor, explanation, create_dt, update_dt FROM INGREDIENT WHERE 1=1
AND category = #{category}
LIMIT #{page}, #{limit} ) temp ON temp.ingredient_id = i.ingredient_id ORDER BY create_dt DESC

mapper.delete:
DELETE FROM INGREDIENT
WHERE 1=1
AND ingredient_id = #{value}
