mapper.select:
SELECT r.review_id as id, r.title, r.contents, r.likes, r.tea_id, r.mem_id, r.create_dt, r.update_dt FROM REVIEW AS r INNER JOIN ( SELECT review_id, title, contents, likes, tea_id, mem_id, create_dt, update_dt FROM REVIEW WHERE 1=1
AND mem_id = #{memId}
AND tea_id = #{teaId}
ORDER BY create_dt DESC LIMIT #{page}, #{limit} ) temp ON temp.review_id = r.review_id

SELECT count(*) FROM REVIEW WHERE 1=1
AND mem_id = #{memId}
AND tea_id = #{teaId}
ORDER BY create_dt DESC
